import React from 'react';
import PropTypes from 'prop-types';

TaskForm.propTypes = {

};

function TaskForm(props) {
    return (
        <div class="card">
            <div class="panel-header bg-warning">
                <h3 class="card-title">Thêm Công Việc
                <span class="fa fa-times-circle text-right ml-70 d-inline-block"
                        onClick={this.onCloseForm}
                    ></span>
                </h3>
            </div>
            <div class="card-body">
                <form>
                    <div class="form-group">
                        <label>Tên :</label>
                        <input type="text" class="form-control" />
                    </div>
                    <label>Trạng Thái :</label>
                    <select class="form-control" required="required">
                        <option value="1">Kích Hoạt</option>
                        <option value="0">Ẩn</option>
                    </select>
                    <br />
                    <div class="text-center">
                        <button type="submit" class="btn btn-warning">Thêm</button>&nbsp;
                  <button type="submit" class="btn btn-danger">Hủy Bỏ</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default TaskForm;
