import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';
function App() {
  const [isDisplay, setIsDisPlay] = useState(false);
  function openForm() {
    setIsDisPlay(true);
  }
  function closeForm() {
    setIsDisPlay(false);
  }
  var elmTaskForm = isDisplay ? <TaskForm closeForm={closeForm} /> : '';

  return (
    <div className="container">

      <div className="text-center">
        <h1>Quản Lý Công Việc</h1>
        <hr />
      </div>

      <div className="row">
        <div className={isDisplay === true ? "col-xs-4 col-sm-4 col-md-4 col-lg-4" : ""}>
          {elmTaskForm}
        </div>

        <div className={isDisplay === true ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" : "col-xs-12 col-sm-12 col-md-12 col-lg-12"} >
          <button type="button" className="btn btn-primary" onClick={openForm}>
            <span className="fa fa-plus mr-5"></span>Thêm Công Việc
          </button>&nbsp;

        <br />
          <br />

          <Control />

          <br />
          <br />

          <div className="row mt-15">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
              <TaskList />
            </div>
          </div>

        </div>
      </div>
    </div >
  );
}

export default App;
