import React, { useState, useEffect } from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';
function App() {
  const [isDisplay, setIsDisPlay] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [filterName, setFilterName] = useState('');
  const [filterStatus, setFilterStatus] = useState(-1);
  useEffect(() => {
    if (localStorage && localStorage.getItem('tasks')) {
      var tasks = JSON.parse(localStorage.getItem('tasks'));
      setTasks(tasks);
    }
  }, []);

  function openForm() {
    setIsDisPlay(true);
  }
  function closeForm() {
    setIsDisPlay(false);
  }
  function submit(data) {
    console.log('click');
    var __tasks = [...tasks];
    if (data.id === '') {
      data.id = generateID();
    }
    var __task = data;
    __tasks.push(__task);
    setTasks(__tasks);
    localStorage.setItem('tasks', JSON.stringify(__tasks));

  }
  function updateStatus(index) {
    var __tasks = [...tasks];
    __tasks[index].status = !__tasks[index].status;
    localStorage.setItem('tasks', JSON.stringify(__tasks));
    setTasks(__tasks);
  }
  function deleteItem(id) {
    var index = findIndex(id);
    if (index !== -1) {
      console.log(index);
      var __tasks = [...tasks];
      __tasks.splice(index, 1);
      setTasks(__tasks);
      localStorage.setItem('tasks', JSON.stringify(__tasks));
    }
  }
  function onFilter(__name, __status) {
    __status = parseInt(__status);
    setFilterName(__name);
    setFilterStatus(__status);
  }
  function findIndex(id) {
    var res = -1;
    const __tasks = tasks;
    for (var i = 0; i < __tasks.length; i++) {
      if (__tasks[i].id === id) {
        res = i;
        break;
      }
    }
    return res;
  }
  function s4() {
    return Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
  }
  function generateID() {
    return s4() + s4() + '-' + s4() + '-' + s4() + '-' +
      s4() + '-' + s4() + s4() + s4();
  }

  var elmTaskForm = isDisplay ? <TaskForm
    closeForm={closeForm}
    onSubmit={submit}

  /> : '';
  var __tasks = [...tasks];
  if (filterName) {
    __tasks = __tasks.filter(function (task) {
      return task.name.toLowerCase().indexOf(filterName.toLowerCase()) > -1;
    });
  }
  return (
    <div className="container">

      <div className="text-center">
        <h1>Quản Lý Công Việc</h1>
        <hr />
      </div>

      <div className="row">
        <div className={isDisplay === true ? "col-xs-4 col-sm-4 col-md-4 col-lg-4" : ""}>
          {elmTaskForm}
        </div>

        <div className={isDisplay === true ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" : "col-xs-12 col-sm-12 col-md-12 col-lg-12"} >
          <button type="button" className="btn btn-primary" onClick={openForm}>
            <span className="fa fa-plus mr-5"></span>Thêm Công Việc
          </button>&nbsp;

        <br />
          <br />

          <Control />

          <br />
          <br />

          <div className="row mt-15">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
              <TaskList
                tasks={__tasks}
                updateStatus={updateStatus}
                deleteItem={deleteItem}
                onFilter={onFilter}
              />
            </div>
          </div>

        </div>
      </div>
    </div >
  );
}

export default App;
