import React, { useState } from 'react';
import PropTypes from 'prop-types';

TaskForm.propTypes = {

};
const [state, setState] = useState({
    id: '',
    name: '',
    status: false
});
function TaskForm(props) {
    function closeForm() {
        props.closeForm();
    }
    function onChange(e) {
        var target = e.target;
    }

    return (
        <div className="card">
            <div className="panel-header bg-warning">
                <h3 className="card-title">Thêm Công Việc
                    <span
                        className="fa fa-times-circle text-right ml-70 d-inline-block"
                        onClick={closeForm}

                    ></span>
                </h3>
            </div>
            <div className="card-body">
                <form>
                    <div className="form-group">
                        <label>Tên :</label>
                        <input
                            type="text"
                            className="form-control"
                            required="required"
                            name="name"
                        />
                    </div>
                    <label>Trạng Thái :</label>
                    <select
                        className="form-control"
                        required="required"
                        name="status"
                        value={state.status}
                        onChange={onChange}
                    >
                        <option value={true}>Kích Hoạt</option>
                        <option value={false}>Ẩn</option>
                    </select>
                    <br />
                    <div className="text-center">
                        <button type="submit" className="btn btn-warning">Thêm</button>&nbsp;
                  <button type="submit" className="btn btn-danger">Hủy Bỏ</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default TaskForm;
