import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';
function App() {
  const [isDisplay, setIsDisPlay] = useState(false);
  const [tasks, setTasks] = useState([
    {
      id: 1,
      name: 'Ăn',
      status: false
    }
  ]);
  function openForm() {
    setIsDisPlay(true);
  }
  function closeForm() {
    setIsDisPlay(false);
  }
  function submit(data) {
    console.log('clik');
    var __tasks = tasks;
    var __task = data;
    __tasks.push(__task);
    setTasks(__tasks);

  }
  var elmTaskForm = isDisplay ? <TaskForm
    closeForm={closeForm}
    onSubmit={submit}

  /> : '';
  var __tasks = tasks;
  console.log(__tasks);
  return (
    <div className="container">

      <div className="text-center">
        <h1>Quản Lý Công Việc</h1>
        <hr />
      </div>

      <div className="row">
        <div className={isDisplay === true ? "col-xs-4 col-sm-4 col-md-4 col-lg-4" : ""}>
          {elmTaskForm}
        </div>

        <div className={isDisplay === true ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" : "col-xs-12 col-sm-12 col-md-12 col-lg-12"} >
          <button type="button" className="btn btn-primary" onClick={openForm}>
            <span className="fa fa-plus mr-5"></span>Thêm Công Việc
          </button>&nbsp;

        <br />
          <br />

          <Control />

          <br />
          <br />

          <div className="row mt-15">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
              <TaskList
                tasks={__tasks}
              />
            </div>
          </div>

        </div>
      </div>
    </div >
  );
}

export default App;
